import React, { useState, useEffect } from 'react';

const EffectComponent = () => {

  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const timer = setTimeout(setTime(new Date()), 1000);
    return () => clearTimeout(timer);
  });
}

export default EffectComponent;
